namespace WPKernel\Showcase\Capability;

use WP_Error;
use WP_REST_Request;

// WPK:BEGIN AUTO
final class Capability
{
    private const POLICY_MAP = [];

    private const FALLBACK = [
        "appliesTo" => "resource",
        "capability" => "manage_options",
    ];

    /**
     * Create a permission callback closure for a capability.
     */
    public static function callback(string $capability_key): callable
    {
        return static function (WP_REST_Request $request) use ($capability_key) {
            return self::enforce($capability_key, $request);
        };
    }

    /**
     * Evaluate a capability against the current user.
     * @return bool|WP_Error
     */
    public static function enforce(string $capability_key, WP_REST_Request $request)
    {
        $definition = self::get_definition($capability_key);
        $capability = $definition["capability"] ?? self::FALLBACK["capability"];
        $scope = $definition["appliesTo"] ?? self::FALLBACK["appliesTo"];

        if ("object" === $scope) {
            $binding = self::get_binding($definition) ?? "id";
            $object_id = $request->get_param($binding);
            if (null === $object_id) {
                return self::create_error(
                    "wpk_capability_object_missing",
                    sprintf(
                        'Object identifier parameter "%s" missing for capability "%s".',
                        $binding,
                        $capability_key,
                    ),
                );
            }

            $allowed = current_user_can($capability, $object_id);
        } else {
            $allowed = current_user_can($capability);
        }

        if ($allowed) {
            return true;
        }

        return self::create_error(
            "wpk_capability_denied",
            "You are not allowed to perform this action.",
            ["capability" => $capability_key, "capability" => $capability],
        );
    }

    /**
     * Retrieve the configuration for a capability key.
     */
    private static function get_definition(string $capability_key): array
    {
        if (isset(self::POLICY_MAP[$capability_key])) {
            return self::POLICY_MAP[$capability_key];
        }

        return self::FALLBACK;
    }

    /**
     * Resolve the request parameter used for object capabilities.
     */
    private static function get_binding(array $definition): ?string
    {
        $binding = $definition["binding"] ?? null;
        if (is_string($binding) && $binding !== "") {
            return $binding;
        }

        return null;
    }

    /**
     * Create a consistent WP_Error instance for capability failures.
     */
    private static function create_error(
        string $code,
        string $message,
        array $context = [],
    ): WP_Error {
        $payload = array_merge(["status" => 403], $context);

        return new WP_Error($code, $message, $payload);
    }
}
