        $previous = $this->prepareJobResponse($post, $request);
        $deleted = wp_delete_post($post->ID, true);
        if (false === $deleted) {
            return new WP_Error(
                "wpk_job_delete_failed",
                "Unable to delete Job.",
                ["status" => 500],
            );
        }

        return [
            "deleted" => true,
            "id" => (int) $post->ID,
            "previous" => $previous,
        ];
