        $job_typeMeta = is_string($job_typeMeta)
            ? $job_typeMeta
            : (string) $job_typeMeta;
        $data["job_type"] = $job_typeMeta;
        $remote_capabilityMeta = get_post_meta($post->ID, "remote_capability", true);
